import React from 'react';

class App extends React.Component{
	 constructor(props) {
      super(props);
      this.state = {login: '', password: '',name : '',show : false};

    }

    onChangevalue = (event) => {

      	this.setState({[event.target.name] : event.target.value});
    }
    show = () => {
    	this.setState({show : true});
    }
    hide = () => {

    	this.setState({login: '', password: '',name : '',show : false});
    }
    onSubmit = (e) =>{
    	e.preventDefault();
    	this.setState({show : true});
    }
    render() {

    	let { name, login, password, show } = {...this.state};


	    return (

	    	<div>

		        <input type="text" name="name" value={name} onChange={this.onChangevalue} autoComplete="off"/>
		       <input type="text" name="login" value={login} onChange={this.onChangevalue} autoComplete="off"/>

	            <input type="password" name="password" value={password} onChange={this.onChangevalue} />
	            <input type="button"  onClick={this.onSubmit} value="Show values"
	                             />
	           <input type="button"  value="Hide" onClick={this.hide} />               
	<p>{this.state.password}</p>
	          	<br />
    			{show && <p> { props.name } { props.login } { password } </p>}
	        </div>
	    );
    }
}

function Showvalues(props) {
	let pass = "";
	pass = new Array(props.password.length + 1 ).join('*');
	return (<p> { props.name } { props.login } { pass } </p>);
}


export default App;
