import React from 'react';

class App extends React.Component{
	 constructor(props) {
      super(props);
      this.state = {login: '', password: '',name : '' };

    }

    onChangevalue = (event) => {

      	this.setState({[event.target.name] : event.target.value});
    }
    show = () => {
		let { name, login, password } = {...this.state};
		document.getElementById("pid").innerHTML = name +' '+ login + ' ' + password;
    	this.setState(state =>({login: '', password: '',name : '' }));
    }
    hide = () => {

    	this.setState({login: '', password: '',name : '',show : false});
    }
    onSubmit = (e) =>{
    	e.preventDefault();
    	this.setState({show : true});
    }
    render() {

    	let { name, login, password } = {...this.state};


	    return (

	    	<div>

		        <input type="text" name="name" value={name} onChange={this.onChangevalue} autoComplete="off"/>
		       <input type="text" name="login" value={login} onChange={this.onChangevalue} autoComplete="off"/>

	            <input type="password" name="password" value={password} onChange={this.onChangevalue} />
	            <input type="button"  onClick={this.show} value="Show values"/>
	           <input type="button"  value="Hide" onClick={this.hide} />               
	          	<br />
    			<p id="pid">  </p>
	        </div>
	    );
    }
}



export default App;
